import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import AddBookForm from './AddBookForm';
import LoginForm from "./LoginForm";
import RegisterForm from "./RegisterForm";
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootswatch/dist/darkly/bootstrap.min.css';

function App() {
    const [books, setBooks] = useState([]);

    useEffect(() => {
        fetch("/books")
            .then(res => res.json())
            .then(data => {
                setBooks(data);
            })
            .catch(error => console.error('Error fetching books:', error));
    }, []); // Fetch books only once when the component mounts

    const handleLogout = () => {
        localStorage.removeItem('user');
        setIsLoggedIn(false);
    };

    return (
        <Router>
            <div className="container my-4">
                <div className="d-flex justify-content-between align-items-center">
                    <div>
                        <Link to="/" className="btn btn-success text-white mr-3">All Books</Link>
                        <Link to="/add-book" className="btn btn-primary text-white">Add Book</Link>
                    </div>
                    <div>
                        {isLoggedIn ? (
                            <button onClick={handleLogout} className="btn btn-outline-danger text-white">Logout</button>
                        ) : (
                            <>
                                <Link to="/register" className="btn btn-outline-primary text-white mr-3">Register</Link>
                                <Link to="/login" className="btn btn-outline-success text-white">Login</Link>
                            </>
                        )}
                    </div>
                </div>
            </div>
            <Routes>
                <Route path="/" element={<BooksList books={books} />} />
                <Route path="/add-book" element={<AddBookForm />} />
                <Route path="/register" element={<RegisterForm />} />
                <Route path="/login" element={<LoginForm />} />
            </Routes>
        </Router>
    );
}

function BooksList({ books }) {
    return (
        <div className="container">
            {books.length === 0 ? (
                <p>Loading...</p>
            ) : (
                <div className="row">
                    {books.map((book, index) => (
                        <div key={index} className="col-md-4 mb-4">
                            <div className="card text-white border-dark">
                                <div className="card-header">
                                    <h5 className="card-title">{book.title}</h5>
                                    <p className="card-text">{book.author}</p>
                                </div>
                                <div className="card-body">
                                    <img src={`http://localhost:5000/uploads/${book.image_url}`} className="card-img-top" alt={book.title} style={{ width: '100%', height: '200px', objectFit: 'cover' }} />
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
}

export default App;
