// EncryptionUtils.js
import CryptoJS from 'crypto-js';

const secretKey = '4673650845381900254985092215910466520715121000334577509120623941149';
const iv = CryptoJS.lib.WordArray.random(16);

export function encryptPassword(password) {
    const ciphertext = CryptoJS.AES.encrypt(password, secretKey, { iv }).toString();
    return { ciphertext, iv };
}
