import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import AddBookForm from './AddBookForm';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootswatch/dist/darkly/bootstrap.min.css';

function App() {
    const [books, setBooks] = useState([]);

    useEffect(() => {
        fetch("/books")
            .then(res => res.json())
            .then(data => {
                setBooks(data);
            })
            .catch(error => console.error('Error fetching books:', error));
    }, []);

    return (
        <Router>
            <div className="container">
                <button type="button" className="btn btn-success text-light">
                    <Link to="/add-book" className="text-white">Add Book</Link>
                </button>
                <div className="row mt-3">
                    {books.length === 0 ? (
                        <p>Loading...</p>
                    ) : (
                        books.map((book, index) => (
                            <div className="card text-white bg-secondary mb-3" style={{ maxWidth: '20rem' }}>
                                <div className="card-header">
                                    <h5 className="card-title">{book.title}</h5>
                                    <p className="card-text">{book.author}</p>
                                </div>
                                <div className="card-body">
                                    <img src={`http://localhost:5000/uploads/${book.image_url}`} className="card-img-top" alt={book.title} style={{ width: '200px', height: '200px', objectFit: 'cover' }} />
                                </div>
                            </div>
                        ))
                    )}
                </div>
            </div>
            <Routes>
                <Route path="/add-book" element={<AddBookForm />} />
            </Routes>
        </Router>
    );
}

export default App;
