import React, { useState, useEffect } from 'react';
import {BrowserRouter as Router, Route, Routes, Link, useParams } from 'react-router-dom';
import Cookies from 'js-cookie';
import AddBookForm from './AddBookForm';
import LoginForm from "./LoginForm";
import RegisterForm from "./RegisterForm";
import BookDetails from './BookDetails';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootswatch/dist/journal/bootstrap.min.css';
import ProfilePage from "./ProfilePage";

function App() {
    const [books, setBooks] = useState([]);
    const [authToken, setAuthToken] = useState(() => Cookies.get('authToken'));

    const handleLogout = () => {
        const token = Cookies.get('authToken');

        fetch("http://localhost:5000/logout", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
                "Authorization": `Bearer ${token}`
            },
            credentials: "include",
        })
            .then(res => {
                console.log('Logout response:', res);
                if (res.ok) {
                    Cookies.remove('authToken');
                    setAuthToken(false);
                } else {
                    throw new Error('Logout failed');
                }
            })
            .catch(error => console.error('Error logging out:', error));
    };


    return (
        <Router>
            <nav className="navbar navbar-expand-lg bg-primary" data-bs-theme="dark">
                <div className="container-fluid">
                    <a className="navbar-brand" href="#">Navbar</a>
                    <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarColor01" aria-controls="navbarColor01" aria-expanded="false" aria-label="Toggle navigation">
                        <span className="navbar-toggler-icon"></span>
                    </button>
                    <div className="collapse navbar-collapse" id="navbarColor01">
                        <ul className="navbar-nav me-auto">
                            <li className="nav-item">
                                <Link to="/" className="btn btn-success text-white mr-3">All Books
                                    <span className="visually-hidden">(current)</span>
                                </Link>
                            </li>
                            {authToken && (
                                <>
                                    <li className="nav-item">
                                        <Link to="/add-book" className="btn btn-primary text-white">Add Book</Link>
                                    </li>
                                    <li className="nav-item">

                                        <Link to="/profile" className="btn btn-info text-white ml-3">My Profile</Link>
                                    </li>
                                </>
                            )}
                            {authToken ? (
                                <li className="nav-item">
                                    <button onClick={handleLogout} className="btn btn-danger text-white">Logout</button>
                                </li>
                            ) : (
                                <>
                                    <li className="nav-item">
                                        <Link to="/register" className="btn btn-info text-white mr-3">Register</Link>
                                    </li>
                                    <li className="nav-item">
                                        <Link to="/login" className="btn btn-danger text-white">Login</Link>
                                    </li>
                                </>
                            )}
                        </ul>
                        <form class="d-flex">
                            <input class="form-control me-sm-2" type="search" placeholder="Search">
                                <button class="btn btn-secondary my-2 my-sm-0" type="submit">Search</button>
                            </input>
                        </form>
                    </div>
                </div>
            </nav>
            <Routes>
                <Route path="/" element={<BooksList setBooks={setBooks} books={books} />} />
                <Route path="/add-book" element={<AddBookForm />} />
                <Route path="/login" element={<LoginForm />} />
                <Route path="/register" element={<RegisterForm />} />
                <Route path="/:bookName" element={<BookDetailsPage books={books} />} />
                <Route path="/profile" element={<ProfilePage />} />
            </Routes>
        </Router>
    );
}

function BooksList({ setBooks, books }) {
    useEffect(() => {
        fetch("/books")
            .then(res => res.json())
            .then(data => {
                setBooks(data);
            })
            .catch(error => console.error('Error fetching books:', error));
    }, []);


    return (
        <div className="container">
            {books.length === 0 ? (
                <p>Loading...</p>
            ) : (
                <div className="row">
                    {books.map((book, index) => (
                        <div key={index} className="col-md-4 mb-4">
                            <Link to={`/${encodeURIComponent(book.title)}`} className="text-decoration-none text-dark">
                                <div className="card text-white bg-primary">
                                    <div className="card-header">
                                        <h5 className="card-title">{book.title}</h5>
                                        <p className="card-text">{book.author}</p>
                                    </div>
                                    <div className="card-body">
                                        <img src={`http://localhost:5000/uploads/${book.image_url}`}
                                             className="card-img-top" alt={book.title}
                                             style={{ width: '100%', height: '200px', objectFit: 'cover' }} />
                                    </div>
                                </div>
                            </Link>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
}

function BookDetailsPage({ books }) {
    const { bookName } = useParams();
    const decodedBookName = decodeURIComponent(bookName);
    const book = books.find((book) => book.title === decodedBookName);

    if (!book) {
        return <div>Book not found</div>;
    }

    return <BookDetails book={book} />;
}

export default App;
