import React, { useState, useEffect } from 'react'

function App() {
    const [books, setBooks] = useState([]);

    useEffect(() => {
        fetch("/books")
            .then(res => res.json())
            .then(data => {
                setBooks(data);
            })
            .catch(error => console.error('Error fetching books:', error));
    }, []);

    return (
        <div>
            {books.length === 0 ? (
                <p>Loading...</p>
            ) : (
                <ul>
                    {books.map((book, index) => (
                        <li key={index}>
                            <h3>{book.title}</h3>
                            <p>{book.author}</p>
                            <img src={book.image_url} alt={book.title} />
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
}

export default App
