import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom'; // Import Routes
import AddBookForm from './AddBookForm';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootswatch/dist/darkly/bootstrap.min.css';

function App() {
    const [books, setBooks] = useState([]);

    useEffect(() => {
        fetch("/books")
            .then(res => res.json())
            .then(data => {
                setBooks(data);
            })
            .catch(error => console.error('Error fetching books:', error));
    }, []);

    return (
        <Router>
            <div>
                <button type="button" className="btn btn-success"><Link to="/add-book">Add Book</Link></button>
                {books.length === 0 ? (
                    <p>Loading...</p>
                ) : (
                    <ul>
                        {books.map((book, index) => (
                            <li key={index}>
                                <h3>{book.title}</h3>
                                <p>{book.author}</p>
                                <img src={`http://localhost:5000/uploads/${book.image_url}`} alt={book.title} />
                            </li>
                        ))}
                    </ul>
                )}
            </div>
            <Routes>
                <Route path="/add-book" element={<AddBookForm />} /> {/* Use element prop instead of component */}
            </Routes>
        </Router>
    );
}

export default App;
